float S_rsqrt( float number, int iterations ) {
  long i;
  float x2, y;
  const float threehalfs = 1.5F;

  x2 = number * 0.5F;
  y  = 0.01; // initial value of y - the result that we're approximating
  for (int i = 0; i < iterations; i++) {
    y  = y * ( threehalfs - ( x2 * y * y ) );
  }

  return y;
}

